mapper.update:
update doctor set doctor_name = #{doctor_name}, doctor_department = #{doctor_department}, doctor_position = #{doctor_position}, doctor_gender = #{doctor_gender}, doctor_tel = #{doctor_tel}, cert_code = #{cert_code},
active = #{active}
where doctor_id = #{doctor_id}

mapper.select:
select doctor_id,doctor_name,doctor_department,doctor_position,doctor_gender,type,doctor_tel,cert_code,active,create_date
from doctor where active=#{active}

  and cert_code=#{cert_code}

limit #{page},#{limit}
